import styled from "styled-components";
import "./Main.css";
import NewsWrapper from "./NewsWrapper";
import Footer from "./Footer";


const Main = (props)=>{
    return(


<ContainerMain>


<ContainerInner>

<TopSection>
For the best experience use inshorts app on your smartphone


<span>
    <img src="/images/downloadestore.png" alt="" />
</span>

</TopSection>


<NewsWrapper/>






<LoadMore>
<center>
<a >
Load More
</a>
</center>
</LoadMore>


<Footer/>


</ContainerInner>

        </ContainerMain>

    );
};


const ContainerMain=styled.div`
margin:0;
padding:0;
left:0;
/* overflow-y:scroll; */


margin-top:10vh;

background-color:red;
/* z-index:1004; */
/* z-index:10040; */
/*  */


`;



const ContainerInner=styled.div`
margin:0;
padding:0;
left:0;

`;


const TopSection=styled.div`
margin:auto;
min-width:860px;
max-width:860px;


padding:16px 26px;
font-size:90%;
color:white;
background-color:#ff5f25;


span{
    float:right;
    img{
        height:40px;
    }
}

`;




const LoadMore=styled.div`
margin-top:40px;
margin-bottom:40px;

padding:20px;

/* margin-left:50%; */
/* margin-right:50%; */


a{
    cursor: pointer;
border: 1px solid grey;
    padding: 10px 20px;


}


`;





export default Main;
