import styled from "styled-components";

const Header = (props)=>{
    return(
        <Container>
            <Content>




                <Logo>
                    <a href="/home">
                        <img  src="/images/inshorts.png" alt="" /> 
                    </a>
                </Logo>




            </Content>
        </Container>
    );
};




const Container=styled.div`
background-color:white;
border-bottom:1px solid rgba(0,0,0, 0.08);
left:0;
padding:10px 24px;
position:fixed;
top:0;
width:100vw;
z-index:100;
`;


const Content=styled.div`
/* display:flex; */
/* align-items:center; */
/* margin:0 auto; */
/* min-height:100%; */
/* max-width:1128px; */


`;


const Logo=styled.div`
font-size:0px;
/* left: -200px; */
padding-right:36px;

padding-top:6px;
padding-bottom:4px;

display:flex;
align-items: center;
justify-content: center;
/* margin:auto; */

a{
  img{
height:36px;

}
}
`;


const Search = styled.div`
  opacity: 1;
  flex-grow: 1;
  position: relative;
  & > div {
    max-width: 280px;
    input {
      border: none;
      box-shadow: none;
      background-color: #eef3f8;
      border-radius: 2px;
      color: rgba(0, 0, 0, 0.9);
      width: 218px;
      padding: 0 8px 0 40px;
      line-height: 1.75;
      font-weight: 400;
      font-size: 14px;
      height: 34px;
      border-color: #dce6f1;
      vertical-align: text-top;
    }
  }
`;


const SearchIcon = styled.div`
  width: 40px;
  position: absolute;
  z-index: 1;
  top: 10px;
  left: 2px;
  border-radius: 0 2px 2px 0;
  margin: 0;
  pointer-events: none;
  display: flex;
  justify-content: center;
  align-items: center;
`;




const NavList = styled.li`
  display: flex;
  align-items: center;
  a {
    align-items: center;
    background: transparent;
    display: flex;
    flex-direction: column;
    font-size: 12px;
    font-weight: 400;
    justify-content: center;
    line-height: 1.5;
    min-height: 52px;
    min-width: 80px;
    position: relative;
    text-decoration: none;
    span {
      color: rgba(0, 0, 0, 0.6);
      display: flex;
      align-items: center;
    }
    @media (max-width: 768px) {
      min-width: 70px;
    }
  }
  &:hover,
  &:active {
    cursor: pointer;
    a {
      span {
        color: rgba(0, 0, 0, 0.9);
      }
    }
  }
`;



const SignOut = styled.div`
  position: absolute;
  top: 45px;
  background: white;
  border-radius: 0 0 5px 5px;
  width: 100px;
  height: 40px;
  font-size: 16px;
  transition-duration: 167ms;
  text-align: center;
  display: none;
`;


const User = styled(NavList)`
  a > svg {
    width: 24px;
    border-radius: 50%;
  }
  a > img {
    width: 24px;
    height: 24px;
    border-radius: 50%;
  }
  span {
    display: flex;
    align-items: center;
  }
  &:hover {
    ${SignOut} {
      align-items: center;
      display: flex;
      justify-content: center;
    }
  }
`;


const Work = styled(User)`
  border-left: 1px solid rgba(0, 0, 0, 0.08);
`;


export default Header;
