import styled from "styled-components";
import "./Leftside.css";


const Leftside = (props)=>{
    return(

        <Container>

          <LanguageWrapper>

            <div>
            <a className="active" href="en">
            English
            </a>
             <a href="hin">
             Hindi
             </a>
             </div>

            </LanguageWrapper>

<SeperatorLine>
</SeperatorLine>

<Categories>
<div className="categories-heading">Categories</div>

<CategoriesList>

<a href="#">
  All News
</a>


<a className="active" href="#">
  India
</a>

<a href="#">
  Business
</a>


<a href="#">
  Sports
</a>


<a href="#">
  World
</a>

<a href="#">
  Politics
</a>

<a href="#">
  Technology
</a>

<a href="#">
  Startup
</a>

<a href="#">
  Entertainment
</a>

<a href="#">
  Miscellaneous
</a>

<a href="#">
  Hatke
</a>

<a href="#">
  Science
</a>

<a href="#">
  Automobile
</a>


</CategoriesList>


</Categories>


        </Container>

    );
};


const Container=styled.div`
/* grid-area:leftside; */

padding:0;
min-width:100%;
min-height:100vh;

background-color:rgb(48,48,54);
`;


const LanguageWrapper = styled.div`
padding:36px 30px;

div{
  /* display:inline-block; */
  width:100%;
  height:100%;
  padding:10px;
  border-radius:4px;
overflow:hidden;
}

a{
text-decoration:none;
padding:6px 10px;

border: 1px solid rgba(228, 228, 255, 0.4);

color:white;
}

.active{
  color:rgb(206, 206, 206);
  background-color:rgba(228, 228, 255, 0.4);
}


`;



const SeperatorLine = styled.div`
width:95%;
height:1px;
background-color:#e5e5fd;
margin:auto;
`;

const Categories = styled.div`
padding: 0 8px;
padding-top:14px;

`;



const CategoriesList = styled.div`
padding:20px 8px;
margin:auto;

.active{
  background-color:rgba(228, 228, 255, 0.4);
}


a{
  border-radius:2px;
  padding:8px 36px;
  margin-top:3px;
  display:block;
  text-decoration:none;
color:#e4e4e4;


&:hover{
  background-color:rgba(228, 228, 255, 0.1);
}

}




`;






export default Leftside;
